package com.campaignworkbench.campaignrenderer;

/**
 * Wrapper of the results of the rendering process
 * @param generatedJavaScript JavaScript generated by the template and XML context
 * @param renderedOutput      rendered output of the JavaScript as HTML
 */
public record TemplateRenderResult(String generatedJavaScript, String renderedOutput) {

    /**
     * @return the generated JavaScript from the result
     */
    @Override
    public String generatedJavaScript() {
        return generatedJavaScript;
    }

    /**
     * @return the rendered HTML output from the result
     */
    @Override
    public String renderedOutput() {
        return renderedOutput;
    }
}
